    styles: [".section-collects {\n    max-width: 960px;\n    margin: 0 auto;\n    position: relative;\n}\n\n.collection-buttons {\n    display: flex;\n    align-items: center;\n    margin-top: 10px;\n}\n\n.collection-buttons>span {\n    margin-right: 14px;\n    align-self: center;\n    font-size: 26px;\n}\n\n.collection-buttons button {\n    display: inline-block;\n    background-color: #2C2B57;\n    border: 0;\n    color: white;\n    height: 41px;\n    margin-left: 20px;\n    cursor: pointer;\n    transition: 0.3s;\n}\n\n.collection-buttons .icon {\n    width: 41px;\n    height: 41px;\n}\n\nh2 {\n    margin: 0;\n    font-family: 'Avenir_Black';\n    font-size: 18px;\n    color: #2C2B57;\n    text-align: center;\n    font-weight: normal;\n    padding: 25px 0;\n    box-sizing: border-box;\n}\n\nh3 {\n    font-family: 'Avenir_Roman';\n    font-size: 18px;\n    margin: 0 0 30px 0;\n    color: #2C2B57;\n    text-align: center;\n    font-weight: normal;\n}\n\n.collects-pickdate {\n    display: flex;\n    justify-content: space-between;\n}\n\n.input-date {\n    width: 380px;\n    display: block;\n}\n\n.search-collect {\n    padding: 8px 22px;\n    box-sizing: border-box;\n    margin: 40px auto !important;\n    color: white;\n    font-family: 'Avenir_Black';\n    background-color: #2C2B57;\n    border: 0;\n    font-size: 18px;\n    display: block;\n    cursor: pointer;\n    transition: 0.3s;\n}\n\n.search-collect:active {\n    background-color: #C0BFCC;\n}\n\n.search-collect:focus,\n.green-button {\n    background-color: #41c98e;\n}\n\n.show-collect {\n    font-size: 16px;\n    font-family: 'Avenir_Heavy';\n    color: #2C2B57;\n    display: flex;\n    justify-content: space-between;\n    align-items: center;\n    padding: 30px 0;\n    position: relative;\n}\n\n.show-collect span {\n    display: block;\n}\n\n.show-collect .time {\n    font-family: \"Avenir_Roman\";\n    font-size: 12px;\n}\n\n.collect-hr {\n    border: 0;\n    border-bottom: 1px solid #E3E2E7;\n    width: 